$types[] = array(
	'type_id' => 'IN',
	'name' => 'Info App',
);

$types[] = array(
	'type_id' => 'SC',
	'name' => 'Section',
);

if (isset($data['display']) && !is_null($data['display'])) {
	$new_types = array();
	foreach($types as $key => $value) {
		$new_types[$value['type_id']] = $value['name'];
	}
	$types = $new_types;
};


return $types;


